mapper.insert:
INSERT INTO DEV_BOARD(
					ARTICLE_NO
					,WRITER_ID
					,WRITER_NAME
					,TITLE
					,REGDATE
					,MODDATE
					,READ_CNT
					,CONTENT)
			VALUES(( SELECT NVL(MAX(ARTICLE_NO), 
						0)+1 FROM DEV_BOARD)
					,#{id}
					,#{name}
					,#{title}
					,#{regDate}
					,#{modDate}
					,#{read_cnt}
					,#{content}
